INSERT INTO books ( id,title,author,publisher,"total_page",topic,"brief_content",type,"date_of_storing","created_at","updated_at",quantity,"image_url",location,"current_quantity","seen_by" ) VALUES ( '3','Designing Data Visualizations [Paperback]','Noah Iliinsky','O''Reilly Media (September 29, 2011)','114','1','Data visualization is an efficient and effective medium for communicating large amounts of information, but the design process can often seem like an unexplainable creative endeavor. This concise book aims to demystify the design process by showing you how to use a linear decision-making process to encode your information visually.','1',' September 29, 2011','2012-11-29 16:35:45','2012-11-29 16:35:46','10','51DP8l3ryJL._BO2,204,203,200_PIsitb-sticker-arrow-click,TopRight,35,-76_AA300_SH20_OU01_.jpg',NULL,'10','0' );
