CREATE TABLE "book_categories" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar(255), "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "books" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar(255), "author" varchar(255), "publisher" varchar(255), "total_page" integer, "topic" varchar(255), "brief_content" text, "type" integer, "date_of_storing" date, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "quantity" integer, "image_url" varchar(255), "location" varchar(255), "current_quantity" integer, "seen_by" integer DEFAULT 0);
CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);
CREATE TABLE "transactions" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "book_id" integer, "user_id" integer, "day_borrow" date, "day_return" varchar(255), "date" varchar(255), "status" integer, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "usergroups" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "group_name" varchar(255), "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "users" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "email" varchar(255) DEFAULT '' NOT NULL, "encrypted_password" varchar(255) DEFAULT '' NOT NULL, "reset_password_token" varchar(255), "reset_password_sent_at" datetime, "remember_created_at" datetime, "sign_in_count" integer DEFAULT 0, "current_sign_in_at" datetime, "last_sign_in_at" datetime, "current_sign_in_ip" varchar(255), "last_sign_in_ip" varchar(255), "nickname" varchar(255), "register_date" datetime, "end_date" date, "group" integer, "student_id" varchar(255) NOT NULL, "class_name" varchar(255) NOT NULL, "home_address" varchar(255) NOT NULL, "phone_number" integer NOT NULL, "locale" varchar(255) DEFAULT 'en' NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "books_borrow" integer);
CREATE UNIQUE INDEX "index_users_on_email" ON "users" ("email");
CREATE UNIQUE INDEX "index_users_on_reset_password_token" ON "users" ("reset_password_token");
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
